function initListener(){const e=document.querySelector(".header-button"),t=document.querySelector(".header-nav-list");window.matchMedia("(max-width: 480px)").matches&&t.insertAdjacentHTML("beforeend",'<li class="header-nav-list__item_end">Exit Menu</li>'),e.addEventListener("click",(function(){t.classList.add("open"),e.classList.add("close")}));document.querySelector(".header-nav-list__item_end").addEventListener("click",(function(){t.classList.remove("open"),e.classList.remove("close")}))}initListener();