function initListener(){const e=document.querySelector(".header-button"),n=document.querySelector(".header-nav-list");menuOpen(e,n);menuClose(document.querySelector(".header-nav-list__link_end"),n,e)}function menuOpen(e,n){e.addEventListener("click",(function(){n.classList.add("open"),e.classList.add("close")}))}function menuClose(e,n,t){e.addEventListener("click",(function(){n.classList.remove("open"),t.classList.remove("close")}))}initListener();